/* get list of objects in the database */\nSELECT name, \n       type \nFROM  sysobjects\nORDER BY type, name\n\n/* get list of columns in each table / parameters for each stored procedure */\nSELECT so.name, \n       so.type, \n       sc.name, \n       sc.number, \n       sc.colid, \n       sc.status, \n       sc.type, \n       sc.length, \n       sc.usertype , \n       sc.scale \nFROM   sysobjects  so , \n       syscolumns  sc \nWHERE  so.id = sc.id \nORDER BY so.type, so.name, sc.name\n\n/* get definition of each stored procedure */\nSELECT so.name, \n       so.type, \n       sc.number, \n       sc.text \nFROM   sysobjects  so , \n       syscomments sc \nWHERE  so.id = sc.id \nORDER BY so.type, so.name, sc.number 