SELECT a.NAME_LAST, \n       a.NAME_FIRST, \n       ca.status, \n       Scores.AverageScore,\n       Scores.CountScore\nFROM   application a\n       Inner Join committee_applications ca \n         On a.application_id = ca.application_id\n       Left Join (\n         SELECT application_id, \n                AVG(score) As AverageScore, \n                Count(*) As CountScore\n         FROM   reviews \n         Group By application_id\n         ) As Scores\n         On a.application_id = Scores.application_id