SELECT   b.TABLESPACE\n       , b.segfile#\n       , b.segblk#\n       , ROUND (  (  ( b.blocks * p.VALUE ) / 1024 / 1024 ), 2 ) size_mb\n       , a.SID\n       , a.serial#\n       , a.username\n       , a.osuser\n       , a.program\n       , a.status\n    FROM v$session a\n       , v$sort_usage b\n       , v$process c\n       , v$parameter p\n   WHERE p.NAME = 'db_block_size'\n     AND a.saddr = b.session_addr\n     AND a.paddr = c.addr\nORDER BY b.TABLESPACE\n       , b.segfile#\n       , b.segblk#\n       , b.blocks;