DECLARE @T varchar(max) \nSET @T = \n'<root>\n    <Field FieldRowId="1000">\n        <Items>\n            <Item Name="CODE"/>\n            <Item Name="DATE"/>\n        </Items>\n    </Field>\n    <Field FieldRowId="2000">\n        <Items>\n            <Item Name="CODE"/>\n            <Item Name="DATE"/>\n        </Items>\n    </Field>\n</root>'\n\nDECLARE @X xml\n\nSET @X = CAST(@T as xml)\nSELECT Y.ID.value('../../@FieldRowId', 'int') as FieldID, \n       Y.ID.value('@Name', 'varchar(max)') as "Name"\nFROM @X.nodes('/root/Field/Items/Item') as Y(ID)