SELECT  ARTICLE_NO\nFROM    auctions1\nWHERE   upper(ARTICLE_NAME) LIKE '% x %'\n        AND (str_to_date( ACCESSSTARTS, '%d/%m/%Y %k:%i:%s' ), article_no) >\n        (\n        SELECT  str_to_date(ACCESSSTARTS, '%d/%m/%Y %k:%i:%s'), article_no\n        FROM    auctions1\n        WHERE   ARTICLE_NO = @pk\n        )\nORDER BY\n        str_to_date(ACCESSSTARTS, '%d/%m/%Y %k:%i:%s'), article_no\nLIMIT 1