select top(1)\n    t.DayId\nfrom (\n        select\n            DayId,\n            case\n                when DayId = 3 then 1 -- DayId: 3 = Wednesday\n                when DayId = 4 then 2 -- DayId: 4 = Thursday\n                else 3\n            end DayRank\n        from\n            tDayChores DC\n            inner join tChore C\n                on C.ChoreId = DC.ChoreId\n        where\n            C.ChoreDescription = 'vacuum'\n    ) t\norder by t.DayRank