create table test\n(\n    id int identity(1, 1) not null,\n    val varchar(25) not null\n)\n\ninsert into test values ('&lt; <- ok, &lt <- nok')\n\nwhile 1 = 1\nbegin\n    update test\n        set val = left(val, patindex('%&lt[^;]%', val) - 1) +\n                      '&lt;' +\n                      right(val, len(val) - patindex('%&lt[^;]%', val) - 2)\n    from test\n    where val like '%&lt[^;]%'\n\n    IF @@ROWCOUNT = 0 BREAK\nend\n\nselect * from test