SELECT  *\nFROM    (\n        SELECT  *\n        FROM    (\n                SELECT  *\n                FROM    mytable\n                WHERE   value > 10000\n                ORDER BY\n                        value\n                )\n        UNION ALL\n        SELECT  *\n        FROM    (\n                SELECT  *\n                FROM    mytable\n                ORDER BY\n                        value DESC\n                )\n        )\nWHERE   rownum = 1