begin\ndeclare @n int, @rows int\n\n    select @rows = count(*) from sourcetable\n\n    select @n=0\n\n    while @n < @rows\n    begin\n\n        insert into desttable\n        select top 2000 * \n        from sourcetable\n        where id_sourcetable not in (select top (@n) id_sourcetable \n                    from sourcetable \n                    order by id_sourcetable)\n        order by id_sourcetable\n\n        select @n=@n+2000\n    end\nend