SELECT  E.EmployeeName,\n        (\n        SELECT  SUM(amount)\n        FROM    MoneyTransactions m\n        WHERE   M.EmployeeID = E.EmployeeID\n        ) AS TotalAmount,\n        (\n        SELECT  SUM(amount)\n        FROM    BudgetTransactions m\n        WHERE   M.EmployeeID = E.EmployeeID\n        ) AS BudgetAmount,\n        (\n        SELECT  SUM(hours)\n        FROM    TimeTransactions m\n        WHERE   M.EmployeeID = E.EmployeeID\n        ) AS TotalHours,\n        (\n        SELECT  SUM(hours)\n        FROM    TimeBudgetTransactions m\n        WHERE   M.EmployeeID = E.EmployeeID\n        ) AS BudgetHours\nFROM    Employees E