SELECT  ctz\nFROM    (\n        SELECT  shecdule.*,\n                CONVERT_TZ(\n                if(timestamp_start > last_update, timestamp_start, last_update), \n                'GMT', \n                user.timezone\n                ) AS ctz\n        FROM    shecdule\n        INNER JOIN\n                user\n        ON      user.user_id = s.user_id\n        ) s\nWHERE  ctz < CURRENT_TIMESTAMP()